package com.example.offlinepplworkoutapp.data.dao
import androidx.room.Dao import androidx.room.Insert import androidx.room.Query import androidx.room.Update import androidx.room.Delete import androidx.room.Transaction import com.example.offlinepplworkoutapp.data.entity.TemplateExercise import com.example.offlinepplworkoutapp.data.entity.Exercise import kotlinx.coroutines.flow.Flow
/** * Data Access Object for TemplateExercise operations */ @Dao interface TemplateExerciseDao {
@Query("SELECT * FROM template_exercises WHERE templateId = :templateId ORDER BY orderIndex") suspend fun getExercisesForTemplate(templateId: Int): List<TemplateExercise>
@Query(""" SELECT e.* FROM exercises e INNER JOIN template_exercises te ON e.id = te.exerciseId WHERE te.templateId = :templateId ORDER BY te.orderIndex """) suspend fun getExerciseDetailsForTemplate(templateId: Int): List<Exercise>
@Query(""" SELECT te.*, e.name as exerciseName, e.primaryMuscle, e.equipment FROM template_exercises te INNER JOIN exercises e ON te.exerciseId = e.id WHERE te.templateId = :templateId ORDER BY te.orderIndex """) fun getTemplateExercisesWithDetails(templateId: Int): Flow<List<TemplateExerciseWithDetails>>
@Query("SELECT * FROM template_exercises WHERE id = :id") suspend fun getTemplateExerciseById(id: Int): TemplateExercise?
@Insert suspend fun insertTemplateExercise(templateExercise: TemplateExercise): Long
@Insert suspend fun insertTemplateExercises(templateExercises: List<TemplateExercise>)
@Update suspend fun updateTemplateExercise(templateExercise: TemplateExercise)
@Delete suspend fun deleteTemplateExercise(templateExercise: TemplateExercise)
@Query("DELETE FROM template_exercises WHERE templateId = :templateId") suspend fun deleteExercisesForTemplate(templateId: Int): Int
@Query("DELETE FROM template_exercises") suspend fun deleteAll(): Int
@Query("SELECT COUNT(*) FROM template_exercises WHERE templateId = :templateId") suspend fun getExerciseCountForTemplate(templateId: Int): Int
@Query("SELECT MAX(orderIndex) FROM template_exercises WHERE templateId = :templateId") suspend fun getMaxOrderIndexForTemplate(templateId: Int): Int?
@Query("UPDATE template_exercises SET orderIndex = orderIndex - 1 WHERE templateId = :templateId AND orderIndex > :deletedIndex") suspend fun reorderAfterDeletion(templateId: Int, deletedIndex: Int) }
/** * Data class for template exercises with exercise details */ data class TemplateExerciseWithDetails( val id: Int, val templateId: Int, val exerciseId: Int, val orderIndex: Int, val sets: Int, val reps: Int, val restSeconds: Int, val weight: Double, val notes: String, val isSuperset: Boolean, val supersetGroup: Int, val exerciseName: String, val primaryMuscle: String, val equipment: String )

package com.example.offlinepplworkoutapp.data.dao

import androidx.room.Dao
import androidx.room.Insert
import androidx.room.Query
import androidx.room.Update
import androidx.room.Delete
import com.example.offlinepplworkoutapp.data.entity.WorkoutTemplate
import kotlinx.coroutines.flow.Flow

/**
 * Data Access Object for WorkoutTemplate operations
 */
@Dao
interface WorkoutTemplateDao {

    @Query("SELECT * FROM workout_templates WHERE isActive = 1 ORDER BY category, name")
    fun getAllActiveTemplates(): Flow<List<WorkoutTemplate>>

    @Query("SELECT * FROM workout_templates WHERE category = :category AND isActive = 1 ORDER BY name")
    fun getTemplatesByCategory(category: String): Flow<List<WorkoutTemplate>>

    @Query("SELECT * FROM workout_templates WHERE id = :templateId")
    suspend fun getTemplateById(templateId: Int): WorkoutTemplate?

    @Query("SELECT * FROM workout_templates WHERE category = :category AND difficulty = :difficulty AND isActive = 1")
    suspend fun getTemplatesByCategoryAndDifficulty(category: String, difficulty: String): List<WorkoutTemplate>

    @Insert
    suspend fun insertTemplate(template: WorkoutTemplate): Long

    @Insert
    suspend fun insertTemplates(templates: List<WorkoutTemplate>)

    @Update
    suspend fun updateTemplate(template: WorkoutTemplate)

    @Delete
    suspend fun deleteTemplate(template: WorkoutTemplate)

    @Query("DELETE FROM workout_templates")
    suspend fun deleteAll(): Int

    @Query("SELECT COUNT(*) FROM workout_templates")
    suspend fun getTemplateCount(): Int

    @Query("UPDATE workout_templates SET lastUsedDate = :date WHERE id = :templateId")
    suspend fun updateLastUsedDate(templateId: Int, date: String)

    @Query("SELECT * FROM workout_templates WHERE isCustom = 1 AND isActive = 1 ORDER BY name")
    fun getCustomTemplates(): Flow<List<WorkoutTemplate>>

    @Query("SELECT * FROM workout_templates WHERE isCustom = 0 AND isActive = 1 ORDER BY category, name")
    fun getPredefinedTemplates(): Flow<List<WorkoutTemplate>>
}

package com.example.offlinepplworkoutapp.data.entity
import androidx.room.Entity import androidx.room.PrimaryKey import androidx.room.ForeignKey
/** * Template Exercise Junction Entity - Links exercises to workout templates * * This entity defines which exercises belong to each template and their configuration * (sets, reps, rest periods, order). This allows for flexible template composition. */ @Entity( tableName = "template_exercises", foreignKeys = [ ForeignKey( entity = WorkoutTemplate::class, parentColumns = ["id"], childColumns = ["templateId"], onDelete = ForeignKey.CASCADE ), ForeignKey( entity = Exercise::class, parentColumns = ["id"], childColumns = ["exerciseId"], onDelete = ForeignKey.CASCADE ) ] ) data class TemplateExercise( @PrimaryKey(autoGenerate = true) val id: Int = 0,
val templateId: Int, // FK to workout_templates val exerciseId: Int, // FK to exercises val orderIndex: Int, // Exercise order in template (0, 1, 2, etc.) val sets: Int, // Default sets for this exercise in this template val reps: Int, // Default reps for this exercise val restSeconds: Int, // Recommended rest between sets (in seconds)
// Optional fields for advanced configurations val weight: Double = 0.0, // Suggested starting weight (if applicable) val notes: String = "", // Exercise-specific notes for this template val isSuperset: Boolean = false, // For future superset implementation val supersetGroup: Int = 0 // Group ID for supersets (0 = no superset) )
